import React , {Component} from 'react';
import {UserContext} from './Contact';

export default class Toppings extends Component {

    constructor(props) {

        super(props);
        this.state = {
            checked: true,
          }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }


      handleChange(event) {
        this.setState({sauce: event.target.value});
      }

      handleSubmit(event){
        this.setState({
          formSubmitted:true
          })
      }

    render(){
        return (
            <div>
            {<h1>
            Choose your extra toppings!
            </h1> }

            {/* <UserContext.Consumer>
            {value => <div>Hello: {value}</div>}
            </UserContext.Consumer> */}

            <h2>The sauce and crust are: {this.props.sauce} + {this.props.crust}</h2>

            <form>
            <label>
            Pepperoni:
            <input
                name="pepperoni"
                type="checkbox"
                checked="checked"
                onChange={this.handleInputChange}
                />
            </label>

            <label>
            Mushroom:
            <input
                name="mushroom"
                type="checkbox"
                checked="checked"
                onChange={this.handleInputChange}
                />
            </label>


            <label>
            Onion:
            <input
                name="onion"
                type="checkbox"
                checked="checked"
                onChange={this.handleInputChange}
                />
            </label>

            <label>
            sauge:
            <input
                name="sauge"
                type="checkbox"
                checked="checked"
                onChange={this.handleInputChange}/>
            </label>

            <label>
            cheese:
            <input
                name="cheese"
                type="checkbox"
                checked="checked"
                onChange={this.handleInputChange}/>
            </label>


            <label>
            olive:
            <input
                name="olive"
                type="checkbox"
                checked="checked"
                onChange={this.handleInputChange}/>
            </label>


              <input type="submit" value="Submit" />
            </form>

            </div>       
        );   
    }


}
