const initialState = {

    contact: {
        name: '',
        email: '',
        address: '',
        phone_number: ''
    },
    sauce: {
        sauce: '',
        crust: '',
     },

    toppings:{
        pepperoni: false,
        mushroom: false,
        onion: false,
        sausage: false,
        cheese: false,
        olive: false,
    }
}


function reducer(state = initialState, action) {  

    switch(action.type) {
        //Updates the user data to edit.data
        case 'UPDATE_CONTACT':
            return{
            ...state,
            contact: action.payload 
            }

        //updates the pizza sauce and crust data
        case 'UPDATE_SAUCE':
            return{
                ...state,
                sauce: action.payload 
                }

        //updates the toppings array, removing item if already in array
        case 'UPDATE_TOPPINGS':
            return{
                ...state,
                toppings: action.payload 
                }

        default:
            return state
    }
}

export default reducer;
