import React , {Component} from 'react';
import {UserContext} from './Contact';


export default class Toppings extends Component {

    constructor(props) {

        super(props);
        this.state = {
            peperroniChecked: false,
            mushroomChecked: false,
            onionChecked: false,
            sausageChecked: false,
            cheeseChecked: false,
            oliveChecked: false


          }

        this.handlePepperoniChange = this.handlePepperoniChange.bind(this);
        this.handleSausageChange = this.handleSausageChange.bind(this);
        this.handleMushRoomChange = this.handleMushroomChange.bind(this);
        this.handleCheeseChange = this.handleCheeseChange.bind(this);
        this.handleOliveChange = this.handleOliveChange.bind(this);
        this.handleOnionChange = this.handleOnionChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }


      handlePepperoniChange(event) {
        this.setState({peperroniChecked: event.target.checked});
      }

      handleSausageChange(event) {
          this.setState({sausageChecked: event.target.checked});
      }
      handleMushroomChange(event) {
          this.setState({mushroomChecked: event.target.checked});
      }

      handleOnionChange(event) {
        this.setState({onionChecked: event.target.checked});
    }

    handleCheeseChange(event) {
        this.setState({cheeseChecked: event.target.checked});
    }

    handleOliveChange(event) {
        this.setState({oliveChecked: event.target.checked});
    }

      handleSubmit(event){        

        this.setState({
          formSubmitted: true
          })
      }

    render(){
        return (
            <div>
            {<h1>
            Choose your extra toppings!
            </h1> }

            {/* <UserContext.Consumer>
            {value => <div>Hello: {value}</div>}
            </UserContext.Consumer> */}

            <h2>The sauce and crust are: {this.props.sauce} + {this.props.crust}</h2>

            <form  onSubmit = {()=> this.handleSubmit()}>
            <label>
            Pepperoni:
            <input
                name="pepperoni"
                type="checkbox"
                check={this.state.peperroniChecked}
                onChange={this.handlePepperoniChange}
                />
            </label>

            <label>
            Mushroom:
            <input
                name="mushroom"
                type="checkbox"
                check={this.state.mushroomChecked}
                onChange={this.handleInputChange}
                />
            </label>


            <label>
            Onion:
            <input
                name="onion"
                type="checkbox"
                check={this.state.onionChecked}
                onChange={this.handleInputChange}
                />
            </label>

            <label>
            sausage:
            <input
                name="sausage"
                type="checkbox"
                check={this.state.sausageChecked}
                onChange={this.handleSausageInputChange}/>
            </label>

            <label>
            cheese:
            <input
                name="cheese"
                type="checkbox"
                check={this.state.cheeseChecked}
                onChange={this.handleInputChange}/>
            </label>


            <label>
            olive:
            <input
                name="olive"
                type="checkbox"
                check={this.state.oliveChecked}
                onChange={this.handleChange}/>
            </label>


              <input type="submit" value="Submit" />
            </form>

            </div>       
        );   
    }


}
