import React , {Component}from 'react';
import {UserContext} from './Contact';
import {SauceContext} from './Sauce';

export default class Order extends Component {


    render(){
        return (


        <div>
        <h2>This is a final page for Orders
            + {this.props.pepperoni && <h1>pepperoni for extra topping</h1>} + {this.props.sausage && <h1>sausage for extra topping</h1>}
        </h2>

        <UserContext.Consumer>
                {value => <div>
                    <h1>Customer Info:</h1>
                    <h3>Hello: {value.name}</h3>
                    <h3>Email: {value.email}</h3>
                    <h3>Address: {value.address}</h3>
                    <h3>Phone Number: {value.phone_number}</h3>
                </div>}
        </UserContext.Consumer>    


        <SauceContext.Consumer>
                {value => <div>
                  <h3>Sauce Level: {value.sauce}</h3>
                  <h3>Crust Type: {value.crust}</h3>
                  </div>}
        </SauceContext.Consumer>


        <h2>This is a final page for Orders
            + {this.props.pepperoni && <h1>pepperoni for extra topping</h1>} + {this.props.sausage && <h1>sausage for extra topping</h1>}
        </h2>



        </div>



        );
        }

}
