export const UPDATE_CONTACT = 'UPDATE_CONTACT';
export const UPDATE_SAUCE = 'UPDATE_SAUCE';
export const UPDATE_TOPPINGS = 'UPDATE_TOPPINGS';

export default function updateContact(payload){
    return {
        type: "UPDATE_CONTACT", 
        payload
    };
}


export function updateSauce(payload){
    return {
        type: "UPDATE_SAUCE", 
        payload
    };
}


export function updateToppings(payload){
    return {
        type: "UPDATE_TOPPINGS", 
        payload
    };
}
