import React , {Component} from 'react';
import { connect } from 'react-redux';
import {UserContext} from './Contact';
import {SauceContext} from './Sauce';
import Order from './Order';
import { updateToppings } from '../action';

export class Toppings extends Component {

    constructor(props) {

        super(props);
        this.state = {

          values:{
            toppings: []
            }

        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit= this.handleSubmit.bind(this);

      }


      handleChange(event) {
        const name = event.target.name;
        const value = event.target.value;

        this.setState({
            values: {...this.state.values, [name]: value}
          }, 
          () => this.props.dispatch(updateToppings(this.state.values))
        );
      }


      handleSubmit(event){
        alert("Pepperoni checked: " + this.state.pepperoni + " Sausage Checked: " + this.state.sausage);        
        this.setState({
          formSubmitted: true
          })
      }

    render(){
        if (this.state.formSubmitted === true){
            return(
            <Order />

            )
          }


        return (
            <div>
            {<h1>
            Choose your extra toppings!
            </h1> }

            <Order />




            <form  onSubmit = {()=> this.handleSubmit()}>
            <label>
            Pepperoni:
            <input
                name="pepperoni"
                type="checkbox"
                check={this.state.pepperoni}
                onChange={this.handleChange}
                />
            </label>

            <label>
            Mushroom:
            <input
                name="mushroom"
                type="checkbox"
                check={this.state.mushroom}
                onChange={this.handleChange}
                />
            </label>


            <label>
            Onion:
            <input
                name="onion"
                type="checkbox"
                check={this.state.onion}
                onChange={this.handleChange}
                />
            </label>

            <label>
            sausage:
            <input
                name="sausage"
                type="checkbox"
                check={this.state.sausage}
                onChange={this.handleChange}/>
            </label>

            <label>
            cheese:
            <input
                name="cheese"
                type="checkbox"
                check={this.state.cheese}
                onChange={this.handleChange}/>
            </label>


            <label>
            olive:
            <input
                name="olive"
                type="checkbox"
                check={this.state.olive}
                onChange={this.handleChange}/>
            </label>


              <input type="submit" value="Submit" />
            </form>

            </div>       
        );   
    }


}

export default connect()(Toppings);
