import React , {Component}from 'react';
import Sauce from './Sauce';

import {
  BrowserRouter as Redirect
} from "react-router-dom";


const UserContext = React.createContext({
      name: '',
      email: '',
      address: '',
      phone_number: '',
      formSubmitted:false
});

class Contact extends Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      address: '',
      phone_number: '',
      formSubmitted:false
      }

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleInputChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value

    });
  }

  handleSubmit(event){

    this.setState({
      formSubmitted:true
      })
  }

    render(){

      if (this.state.formSubmitted === true){
        return(
          <UserContext.Provider value={this.state}>
            {this.props.Sauce}
            <Redirect to= "/Sauce" />
          </UserContext.Provider>


        )
      }

      return(

        <form onSubmit = {()=> this.handleSubmit()}>
          <label>
          Name:
          <input type="text" name="name"  value={this.state.name} onChange={this.handleInputChange} />
          </label>
          <br/>

          <label>
          Email:
          <input type="text" name="email" value={this.state.email} onChange={this.handleInputChange} />
          </label>
          <br/>

          <label>
          Address:
          <input type="text" name="address" value={this.state.address} onChange={this.handleInputChange} />
          </label>
          <br/>

          <label>
          Phone Number:
          <input type="text" name="phone_number" value={this.phone_number} onChange={this.handleInputChange} />
          </label>
          <br/>

        <input type="submit" value="Submit" />


          <div>
            <h1>{this.state.name}</h1>

            <h1>{this.state.email}</h1>

            <h1>{this.state.address}</h1>

            <h1>{this.state.phone_number}</h1>

          </div>
      </form>

      );
    }
  }

  export default Contact;
