import React , {Component}from 'react';

import Sauce from './components/Sauce';

import {
  BrowserRouter as Router,Route,Redirect
} from "react-router-dom";



export default class Contact extends Component {


  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      address: '',
      phone_number: '',
      onSubmitted: false
      };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleInputChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value

    });
  }

  handleSubmit(event){
    event.preventDefault();
    this.setState({
      formSubmitted:true
      })
  }

    render(){

      if (this.state.formSubmitted === true){
        return(
          <div>
            <Route path = "/sauces" component={Sauce} />
            <Redirect to= "/sauces" />
          </div>
        )
      }

      return(

      <form>
        <label>
        Name:
        <input type="text" name="name"  value={this.state.name} onChange={this.handleInputChange} />
        </label>
        <br/>

        <label>
        Email:
        <input type="text" name="email" value={this.state.email} onChange={this.handleInputChange} />
        </label>
        <br/>

        <label>
        Address:
        <input type="text" name="address" value={this.state.address} onChange={this.handleInputChange} />
        </label>
        <br/>

        <label>
        Phone Number:
        <input type="text" name="phone_number" value={this.phone_number} onChange={this.handleInputChange} />
        </label>
        <br/>

      <input type="submit" value="Submit" />

      <div>
        <h1>{this.state.name}</h1>

        <h1>{this.state.email}</h1>

        <h1>{this.state.address}</h1>

        <h1>{this.state.phone_number}</h1>

      </div>
      </form>

      );
    }
  }
