import React , {Component}from 'react';
import { connect } from 'react-redux';
import {Header} from 'semantic-ui-react'

const toppings = [];

const mapStateToProps = state => {
    return { values : state.contact,
            values1: state.sauce,
            values2: state.toppings
    }
}


const Order = ({values, values1,values2}) => (
    <div class="ui stacked segments"> 
        <div class="ui segment">
            <p> 
                <Header as="h2" color="teal" textAlign="center">Contact Information:</Header>
                <h5>Name: {values.name}</h5>
                <h5>Email: {values.email}</h5>
                <h5>Name: {values.address}</h5>
                <h5>Phone_Number: {values.phone_number}</h5>
            </p> 
        </div>

        <div class="ui segment">
            <p> 
                <Header as="h2" color="teal" textAlign="center">Sauce Level and Crust Types:</Header>
                <h5>Crust: {values1.crust}</h5>
                <h5>Sauce: {values1.sauce}</h5>
            </p> 
        </div>


        <div class="ui segment">
            <p>
                <Header as="h2" color="teal" textAlign="center">Toppings:</Header>

                        {values2.pepperoni && toppings.push("pepperoni")} 
                        <h3>{toppings[0]}</h3>   
                        {values2.mushroom && toppings.push("mushrooms")} 
                        <h3>{toppings[1]}</h3>   
                        {values2.onion && toppings.push("oninon")}
                        <h3>{toppings[2]}</h3>    
                        {values2.sausage && toppings.push("sausage")} 
                        <h3>{toppings[3]}</h3>   
                        {values2.olive && toppings.push("olive")}
                        <h3>{toppings[4]}</h3>   
            </p> 
        </div>
    </div>

);

export default connect(mapStateToProps)(Order);
