import React , {Component}from 'react';
import {UserContext} from './Contact';


export default class Sauce extends Component {

    constructor(props) {
        super(props);
        this.state = {
          name: '',
          email: '',
          address: '',
          phone_number: '',
          formSubmitted:false
          }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }


      handleInputChange(event) {
        const name = event.target.name;
        const value = event.target.value;

        this.setState({
          [name]: value

        });
      }

      handleSubmit(event){

        this.setState({
          formSubmitted:true
          })
      }

    render(){

        if (this.state.formSubmitted === true){
            return(
              <UserContext.Provider value={this.state.name}>
                <Sauce />
                <Redirect to= "/Sauce" />
              </UserContext.Provider>

            )
          }

        return (
            <div>
            {<h2>
            Choose your sauce level and crust types!
            </h2> }

            <UserContext.Consumer>
            {value => <div>Hello: {value}</div>}
            </UserContext.Consumer>

            <form>
              <label>
                Pick your sauce level:
                <select value='extra_sauce'>
                  <option value="extra_sauce">Extra Sauce</option>
                  <option value="less_sauce">Less Sauce</option>
                  <option value="SpinachandFeta">Spinach and Feta</option>
                  <option value="pineaaple">Pineapple</option>
                </select>
              </label>


              <label>
                Pick your crust type:
                <select value='thin'>
                  <option value="thin">Thin</option>
                  <option value="thick">Thick</option>
                  <option value="stuffed">Stuffed</option>
                </select>

              </label>
              <input type="submit" value="Submit" />
            </form>

            </div>       
        );   
    }
}
