import React , {Component} from 'react';
import {UserContext} from './Contact';
import {SauceContext} from './Sauce';
import Order from './Order';

export class Toppings extends Component {

    constructor(props) {

        super(props);
        this.state = {

          values:{
            pepperoni: false,
            mushroom: false,
            onion: false,
            sausage: false,
            cheese: false,
            olive: false,
            formSubmitted: false
            }

        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit= this.handleSubmit.bind(this);

      }


      handleInputChange(event) {
        const name = event.target.name;
        const value = event.target.value;

        this.setState({
            values: {...this.state.values, [name]: value}
          }, 
          () => this.props.dispatch(updateContact(this.state.values))
        );
      }


      handleSubmit(event){
        alert("Pepperoni checked: " + this.state.pepperoni + " Sausage Checked: " + this.state.sausage);        
        this.setState({
          formSubmitted: true
          })
      }

    render(){
        if (this.state.formSubmitted === true){
            return(
            <Order 
            pepperoni = {this.state.pepperoni}
            sausage = {this.state.sausage}
            />

            )
          }


        return (
            <div>
            {<h1>
            Choose your extra toppings!
            </h1> }

            <UserContext.Consumer>
            {value => <div>
                <h1>Hello: {value.name}</h1>
                </div>
            }
            </UserContext.Consumer> 

            <SauceContext.Consumer>
                {value => <div>
                  <h3>Sauce Level: {value.sauce}</h3>
                  <h3>Crust Type: {value.crust}</h3>
                  </div>}
            </SauceContext.Consumer>


            <form  onSubmit = {()=> this.handleSubmit()}>
            <label>
            Pepperoni:
            <input
                name="pepperoni"
                type="checkbox"
                check={this.state.pepperoni}
                onChange={this.handleChange}
                />
            </label>

            <label>
            Mushroom:
            <input
                name="mushroom"
                type="checkbox"
                check={this.state.mushroom}
                onChange={this.handleChange}
                />
            </label>


            <label>
            Onion:
            <input
                name="onion"
                type="checkbox"
                check={this.state.onion}
                onChange={this.handleChange}
                />
            </label>

            <label>
            sausage:
            <input
                name="sausage"
                type="checkbox"
                check={this.state.sausage}
                onChange={this.handleChange}/>
            </label>

            <label>
            cheese:
            <input
                name="cheese"
                type="checkbox"
                check={this.state.cheese}
                onChange={this.handleChange}/>
            </label>


            <label>
            olive:
            <input
                name="olive"
                type="checkbox"
                check={this.state.olive}
                onChange={this.handleChange}/>
            </label>


              <input type="submit" value="Submit" />
            </form>

            </div>       
        );   
    }


}

export default connect()(Toppings);
