import React , {Component}from 'react';
import {UserContext} from './Contact';


export default class Sauce extends Component {

    render(){
        return (
            <div>
            {<h2>
            This is the sauce page
            </h2> }

            <UserContext.Consumer>
            { value => <div>{value}</div>}
            </UserContext.Consumer>

            <form>
              <label>
                Pick your favorite topping:
                <select value='Pepperoni'>
                  <option value="BBQ Chicken">BBQ Chicken</option>
                  <option value="Pepperoni">Pepperoni</option>
                  <option value="Spinach and Feta">Spinach and Feta</option>
                  <option value="pineaple">Pineapple</option>
                </select>
              </label>
              <input type="submit" value="Submit" />
            </form>

            </div>


        );





        }

}
