    //     onion: false,
    //     sausage: false,
    //     cheese: false,
    //     olive: false,
    // }
}


function reducer(state = initialState, action) {  

    switch(action.type) {
        //Updates the user data to edit.data
        case 'UPDATE_CONTACT':

            return{
            ...state,
            contact: action.payload 
            }


        //updates the pizza base data
        case UPDATE_SAUCE:

            return{
                ...state,
                sauce: action.payload 
                }

        //updates the toppings array, removing item if already in array
        case UPDATE_TOPPINGS:

            return{
                ...state,
                toppings: action.payload 
                }


        // case SUBMIT_FORM:

        //         console.log("--- Triggered Form submission ---");
        //         console.log("Form Data - ", state.formValues);
        //         return {
        //             ...state,
        //             message: "Form submitted!!"
        //             };

        default:
            return state
    }
}

export default reducer;
