import React , {Component}from 'react';
import { connect } from 'react-redux';

const toppings = [];

const mapStateToProps = state => {
    return { values : state.contact,
            values1: state.sauce,
            values2: state.toppings
    }
}


const Order = ({values, values1,values2}) => (
    <div class="ui piled segments"> 

        <div class="ui segment">
            <h1>Name: {values.name}</h1>
            <h1>Email: {values.email}</h1>
            <h1>Name: {values.address}</h1>
            <h1>Phone_Number: {values.phone_number}</h1>
        </div>

        <h1>Crust: {values1.crust}</h1>
        <h1>Sauce: {values1.sauce}</h1>
        {values2.pepperoni && toppings.push("pepperoni")} 
        {values2.mushroom && toppings.push("mushrooms")} 
        {values2.onion && toppings.push("oninon")} 
        {values2.sausage && toppings.push("sausage")} 
        {values2.olive && toppings.push("olive")}

        <h1>{toppings}</h1>      
    </div>

);

export default connect(mapStateToProps)(Order);
