import React , {Component}from 'react';
import Sauce from './Sauce';
import Toppings from './Toppings';


import {BrowserRouter as Redirect} from "react-router-dom";
import updateContact from '../action';


export const UserContext = React.createContext()

class Contact extends Component {

  constructor(props) {
    super(props);
    this.state = {
      values: {
      name: '',
      email: '',
      address: '',
      phone_number: ''
      },
      formSubmitted : false
    }

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleInputChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
        values: {...this.state.values, [name]: value}
      }, 
      () => this.props.dispatch(updateContact(this.state.values))
    );
  }

  handleSubmit(event){

    this.setState({
      formSubmitted:true
      })
  }

    render(){

      if (this.state.formSubmitted === true){
        return(

          <UserContext.Provider value={this.state}>
            <Sauce />
            <Redirect to= "/Sauce" />
          </UserContext.Provider>

        )
      }

      return(

        <form onSubmit = {()=> this.handleSubmit()}>
          <p>
          Name:
          <input type="text" name="name"  value={this.state.name} onChange={this.handleInputChange} />
          </p>


          <p>
          Email:
          <input type="text" name="email" value={this.state.email} onChange={this.handleInputChange} />
          </p>

          <p>
          Address:
          <input type="text" name="address" value={this.state.address} onChange={this.handleInputChange} />
          </p>

          <p>
          Phone Number:
          <input type="text" name="phone_number" value={this.phone_number} onChange={this.handleInputChange} />
          </p>

        <input type="submit" value="Submit" />


          <div>
            <h1>{this.state.name}</h1>

            <h1>{this.state.email}</h1>

            <h1>{this.state.address}</h1>

            <h1>{this.state.phone_number}</h1>

          </div>
      </form>

      );
    }
  }

  export default Contact;
