import React , {Component} from 'react';
import {UserContext} from './Contact';
import Order from './Order';

export default class Toppings extends Component {

    constructor(props) {

        super(props);
        this.state = {
            peperroniChecked: false,
            mushroomChecked: false,
            onionChecked: false,
            sausageChecked: false,
            cheeseChecked: false,
            oliveChecked: false,
            formSubmitted: false

          }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit= this.handleSubmit.bind(this);

      }


      handleChange(event) {
        this.setState({peperroniChecked: event.target.checked});
      }


      handleSubmit(event){
        alert("Pepperoni checked: " + this.state.peperroniChecked + " Sausage Checked: " + this.state.sausageChecked);        
        this.setState({
          formSubmitted: true
          })
      }

    render(){
        if (this.state.formSubmitted === true){
            return(
            <Order 
            pepperoni = {this.state.peperroniChecked}
            sausage = {this.state.sausageChecked}
            />


            )
          }


        return (
            <div>
            {<h1>
            Choose your extra toppings!
            </h1> }

            {/* <UserContext.Consumer>
            {value => <div>Hello: {value}</div>}
            </UserContext.Consumer> */}

            <h2>The sauce and crust are: {this.props.sauce} + {this.props.crust}</h2>

            <form  onSubmit = {()=> this.handleSubmit()}>
            <label>
            Pepperoni:
            <input
                name="pepperoni"
                type="checkbox"
                check={this.state.peperroniChecked}
                onChange={this.handlePepperoniChange}
                />
            </label>

            <label>
            Mushroom:
            <input
                name="mushroom"
                type="checkbox"
                check={this.state.mushroomChecked}
                onChange={this.handleMushRoomChange}
                />
            </label>


            <label>
            Onion:
            <input
                name="onion"
                type="checkbox"
                check={this.state.onionChecked}
                onChange={this.handleInputChange}
                />
            </label>

            <label>
            sausage:
            <input
                name="sausage"
                type="checkbox"
                check={this.state.sausageChecked}
                onChange={this.handleSausageChange}/>
            </label>

            <label>
            cheese:
            <input
                name="cheese"
                type="checkbox"
                check={this.state.cheeseChecked}
                onChange={this.handleInputChange}/>
            </label>


            <label>
            olive:
            <input
                name="olive"
                type="checkbox"
                check={this.state.oliveChecked}
                onChange={this.handleChange}/>
            </label>


              <input type="submit" value="Submit" />
            </form>

            </div>       
        );   
    }


}
