import React , {Component} from 'react';
import { connect } from 'react-redux';
import Order from './Order';
import {updateToppings} from '../action';
import {Form,Header} from 'semantic-ui-react';
import "./pizza/Pizza.css";
import "bootstrap/dist/css/bootstrap.min.css";

import Base from "./base/Base";
import Tomato from "./tomato/Tomato";
import Mushroom from "./mushroom/Mushroom";
import Corn from "./corn/Corn";
import Veggie from "./veggie/Veggie";
import ButtonGroup from "./buttonGroup/buttonGroup";

class Toppings extends Component {
  constructor(props) {
    super(props);
    this.state = {
      values:{
      tomato: false,
      mushroom: false,
      veggie: false,
      corn: false
      },
      formSubmitted: false
    },
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit= this.handleSubmit.bind(this);
  }



  clickEventHandler = ingredient => {
    if (ingredient === "tomato") {
      this.setState({
        tomato: !this.state.values.tomato
      });
    } else if (ingredient === "mushroom") {
      this.setState({
        mushroom: !this.state.values.mushroom
      });
    } else if (ingredient === "corn") {
      this.setState({
        corn: !this.state.values.corn
      });
    } else {
      this.setState({
        veggie: !this.state.values.veggie
      });
    }
  };


  handleChange(event) {
    const name = event.target.name;
    const value = event.target.checked;
    this.setState({
        values: {...this.state.values, [name]: value}
      })
  }


  handleSubmit(){     
    this.setState({
      formSubmitted: true},
      () => this.props.dispatch(updateToppings(this.state.values))
      );
  }

  render() {
    const tomato = this.state.tomato && <Tomato />;
    const mushroom = this.state.mushroom && <Mushroom />;
    const corn = this.state.corn && <Corn />;
    const veggie = this.state.veggie && <Veggie />;

    return (
      <div class= "ui segment">
        <Form onSubmit = {()=> this.handleSubmit()}>

        <div className="container mt-5">
          <div className="d-flex justify-content-center">
            <div className="plate">
              <Base />
              {tomato}
              {mushroom}
              {corn}
              {veggie}
            </div>
          </div>
          <ButtonGroup eventHandler={this.clickEventHandler} />
        </div>


        <Form.Field>
              <button class="teal ui button" type="submit" value="Submit"> Sumbit</button>
        </Form.Field>

        </Form>
      </div>
    );
  }
}

export default connect()(Toppings);
