import React , {Component}from 'react';
import Toppings from './Toppings';
import {UserContext} from './Contact';

import {BrowserRouter as Redirect} from "react-router-dom";



export default class Sauce extends Component {

    constructor(props) {
        super(props);
        this.state = {
            sauce: '',
            crust:''
          }

        this.handleChange = this.handleChange.bind(this);
        this.handleChangeCrust = this.handleChangeCrust.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }


      handleChange(event) {
        this.setState({sauce: event.target.value});
      }

      handleChangeCrust(event) {
        this.setState({crust: event.target.value});
      }

      handleSubmit(event){
        alert("Sauce: " + this.state.sauce + " Crust: " + this.state.crust);
        this.setState({
          formSubmitted:true
          })
      }

    render(){

        if (this.state.formSubmitted === true){
            return(
            //   <UserContext.Provider value={this.state.sauce}>
            //     <Toppings />
            //     <Redirect to= "/Toppings" />
            //   </UserContext.Provider>
            <Toppings 
            sauce = {this.state.sauce}
            crust = {this.state.crust}
            />


            )
          }


        return (
            <div>
                {<h2>
                Choose your sauce level and crust types!
                </h2> }

                <UserContext.Consumer>
                {value => <div>Hello: {value}</div>}
                </UserContext.Consumer>

                <form onSubmit = {()=> this.handleSubmit()}>
                <label>
                    Pick your sauce level:
                    <select value={this.state.sauce} onChange = {this.handleChange}>
                    <option value="extra">Extra Sauce</option>
                    <option value="less">Less Sauce</option>
                    <option value="spinach">Spinach and Feta</option>
                    <option value="pineapple">Pineapple</option>
                    </select>
                </label>


                <label>
                    Pick your crust type:
                    <select value={this.state.crust} onChange = {this.handleChangeCrust}>
                    <option value="thin">Thin</option>
                    <option value="thick">Thick</option>
                    <option value="stuffed">Stuffed</option>
                    </select>

                </label>
                <input type="submit" value="Submit" />
                </form>




            </div>       
        );   
    }
}
